function change(obj) {
                if (obj.src == 'bootstrap-3.3.7-dist/fonts/png/glyphicons-194-ok-sign.png') //positive
                    obj.src = 'bootstrap-3.3.7-dist/fonts/png/glyphicons-191-plus-sign.png';  //negative
                else
                    obj.src = 'bootstrap-3.3.7-dist/fonts/png/glyphicons-194-ok-sign.png'; //positive
            };
            

            

